import React, {useState} from 'react';
import './CompareNews.css';


function CompareNews({placeholder, data}) {

    const [filteredData, setFilteredData] = useState([]);
    const [wordEntered, setWordEntered] = useState("");

    const handleFilter = (event) => {
      const searchWord = event.target.value;
      setWordEntered(searchWord);
      const newFilter = data.filter((value) => {
        return value.title.toLowerCase().includes(searchWord.toLowerCase());
      });

      if (searchWord === "") {
        setFilteredData([]);
      } else {
        setFilteredData(newFilter);
      }
    };

    const clearInput = () => {
      setFilteredData([]);
      setWordEntered("");
    };

   let newsList = [
       { name:'Mauritius Times', country: 'Mauritius'},
       { name: 'Seychelles Nation', country: 'Seychelles'},
       { name: 'South China Morning Post', country: 'Hong Kong'},
       { name: 'The Straits Times', country: 'Singapore'},
       { name: 'The Australian', country: 'Australia'},
       { name: 'The New Zealand Herald', country: 'New Zealand'},
       { name: 'Aftenposten', country: 'Norway'},
       { name: '', country: ''},
       { name: 'The Globe and Mail', country: 'Canada'},
       { name: 'USA Today', country: 'United States of America'},
       { name: 'The Santiago TImes', country: 'Chile'},
       { name: 'Buenos Aires Times', country: 'Argentina'}
    ];

    /*const handleChange = (e) => {
        e.preventDefault();
        setInput(e.target.value);
    };

    if(input.length > 0) {
        newsList = newsList.filter((i) => {
            return i.name.toLocaleLowerCase().match(input);
        })
    } */





    return (
        <>
        <div className='heading'>
            <h1>Compare News</h1>
        </div>

        <div className='search-container'>
            <h2> Keyword: </h2>
            <form action='/' method='get' role='search' className='search-form'>
                <input autoComplete='off' name='q' placeholder='Enter a keyword' title='Enter a keyword' type='text' />
                <script async src="https://cse.google.com/cse.js?cx=0e73f5a259cd2c4b2"></script>
            <div className='gcse-searchresults-only'></div>
            </form>
        </div>

        <div className="search">
      <div className="searchInputs">
        <input
          type="text"
          placeholder={placeholder}
          value={wordEntered}
          onChange={handleFilter}
        />
        <div className="searchIcon">
          {filteredData.length === 0 ? (
            <SearchIcon />
          ) : (
            <CloseIcon id="clearBtn" onClick={clearInput} />
          )}
        </div>
      </div>
      {filteredData.length != 0 && (
        <div className="dataResult">
          {filteredData.slice(0, 15).map((value, key) => {
            return (
              <a className="dataItem" href={value.link} target="_blank">
                <p>{value.title} </p>
              </a>
            );
          })}
        </div>
      )}
    </div>
  );
}








        </>
    )
}

export default CompareNews;
